span class="p">(</span><span class="nv">$arguments</span><span class="p">[</span><span class="s2">&quot;file&quot;</span><span class="p">],</span> <span class="nv">$filepath</span><span class="p">);</span></div><div class='line' id='LC23'>	<span class="p">}</span></div><div class='line' id='LC24'>	<span class="k">elseif</span><span class="p">(</span><span class="nv">$_FILES</span><span class="p">[</span><span class="s2">&quot;file&quot;</span><span class="p">][</span><span class="s2">&quot;type&quot;</span><span class="p">]</span> <span class="o">!=</span> <span class="k">NULL</span><span class="p">){</span></div><div class='line' id='LC25'>		<span class="nv">$JSON2CSV</span><span class="o">-&gt;</span><span class="na">JSONfromFile</span><span class="p">(</span><span class="nv">$_FILES</span><span class="p">[</span><span class="s2">&quot;file&quot;</span><span class="p">][</span><span class="s2">&quot;tmp_name&quot;</span><span class="p">]);</span></div><div class='line' id='LC26'>		<span class="nv">$JSON2CSV</span><span class="o">-&gt;</span><span class="na">browserDL</span><span class="p">(</span><span class="s2">&quot;JSON2.CSV&quot;</span><span class="p">);</span></div><div class='line' id='LC27'>	<span class="p">}</span></div><div class='line' id='LC28'>	<span class="k">elseif</span><span class="p">(</span><span class="nv">$_POST</span><span class="p">[</span><span class="s1">&#39;json&#39;</span><span class="p">]</span> <span class="o">!=</span> <span class="k">NULL</span><span class="p">){</span></div><div class='line' id='LC29'>		<span class="nv">$JSON2CSV</span><span class="o">-&gt;</span><span class="na">readJSON</span><span class="p">(</span><span class="nv">$_POST</span><span class="p">[</span><span class="s1">&#39;json&#39;</span><span class="p">]);</span></div><div class='line' id='LC30'>		<span class="nv">$JSON2CSV</span><span class="o">-&gt;</span><span class="na">browserDL</span><span class="p">(</span><span class="s2">&quot;JSON2.CSV&quot;</span><s